import React, { ReactElement } from 'react'
import {User} from '../App';

interface Props {
    initialUsers: User[],
    onChange: (user: User) => void;
    activeChange: (newActive: string) => void;
}

export default function Registration({initialUsers, onChange,activeChange}: Props): ReactElement {
    const newUser = {
        name: '',
        email: '',
        password: ''
    };
  
    const handleChange = (user: User) => {
        onChange(user);
    }

    const handleActive = (newActive: string) => {
        activeChange(newActive);
    }

    const checkMail = (email: string) => {
        for (var i = 0; i < initialUsers.length; i++) {
            if (initialUsers == email) return true;
          }
    }

    const handleSubmit = () => {
        if(newUser.password.length >= 6 && newUser.email.indexOf('@') !== -1 && checkMail(newUser.email)){
            
            handleActive('auth');
            handleChange(newUser);
        }
        else alert("You've incorrectly written password or email! Remember: Password must contain 6 symbols and email must have a '@' symbol")
    }
  
    return (
        <div>
            <div className = "input-label-block">
                <span>Name: </span><input type = "text" onChange = {e => { newUser.name = e.target.value}} required/>
                <span>Email: </span><input type = "text" onChange = {e => { newUser.email = e.target.value}} required/>
                <span>Password: </span><input type = "text" onChange = {e => { newUser.password = e.target.value}} required/>
            </div>
            <div className= "btn-auth-block">
                <button className = 'btn' onClick = {()=> handleActive('')}>Cancel</button>
                <button className = 'btn' onClick = {handleSubmit}>Submit</button>
            </div>
        </div>
        
    )
}
