import React, {useState} from 'react';
import './App.css';
import Auth from './Components/Auth';
import Registration from './Components/Registration';
import Welcome from './Components/Welcome';



interface User {
  id: number,
  name: string,
  email: string,
  password: string
};

const users: User[] = [
 {
  id: 1,
  name: 'Alim',
  email: 'alim.atabay117@gmail.com',
  password: '200017',
 }
];

function App() {
  function MyTemplate(active:string) {
    switch (active) {
      case 'registration':
        return (<Registration />);
      case 'auth':
        return (<Auth />);
      case 'welcome':
        return (<Welcome/>);
      default:
        return (<div></div>);
    }
  }


  const [active, setActive] = useState('');


  return (
    <div>
      <div className = 'main-block'>
          <h1 className = 'title'>Hi, are you new user?</h1>
          <button className="btn" onClick={() => {setActive('auth')}}>Login</button>
          <button className="btn"  onClick={() => {setActive('registration')}}>Sign Up</button>
      </div>
      {MyTemplate(active)}

    </div>
  );
}

export default App;
