import React, {useState} from 'react';
import './App.css';
import Auth from './Components/Auth/Auth';
import Registration from './Components/Registration/Registration';
import Welcome from './Components/Welcome/Welcome';



export interface User {
  // id: number,
  name: string,
  email: string,
  password: string
};

const arrayOfUsers: User[] = [
 {
  // id: 1,
  name: 'Alim',
  email: 'alim.atabay117@gmail.com',
  password: '200017',
 }
];

function App() {
  const [active, setActive] = useState('');
  const [users, setUsers] = useState(arrayOfUsers);
  const [activeUser, setActiveUser] = useState({
    name: '',
    email: '',
    password: ''
  });

  function MyTemplate(active:string) {
    switch (active) {
      case 'registration':
        return (<Registration initialUsers = {users} onChange = {handleChange} activeChange = {handleActive} />);
      case 'auth':
        return (<Auth initialUsers = {users} activeChange = {handleActive} activeUserChange = {handleActiveUser}/>);
      case 'welcome':
        return (<Welcome activeUser = {activeUser}/>);
      default:
        return (<div></div>);
    }
  }

  const handleChange = (user: User) =>{
    setUsers([...users,user])
  };

  const handleActive = (newActive: string) => {
    setActive(newActive);
  };

  const handleActiveUser = (newUser: User) => {
    setActiveUser(newUser);
  }

  return (
    <div className = "welcome-auth-registration-block">
      <div className = 'header-block'>
          <div className = "header-block-title">
            <h1>One Milliom KBTU Stories</h1>
          </div>
          <div className = "header-block-btn">
            <button className="btn" onClick={() => {setActive('auth')}}>Login</button>
            <button className="btn"  onClick={() => {setActive('registration')}}>Sign Up</button>
          </div>
      </div>

      <div className = 'main-block'>
        {MyTemplate(active)}
      </div>
    </div>
  );
}

export default App;
