import React, {useState} from 'react';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom';
import './App.css';
import Auth from './Components/Auth/Auth';
// import Header from './Components/Header/Header';
import Registration from './Components/Registration/Registration';
import Welcome from './Components/Welcome/Welcome';


export interface User {
  name: string,
  email: string,
  password: string
};

const arrayOfUsers: User[] = [
 {
  name: 'Alim',
  email: 'alim.atabay117@gmail.com',
  password: '200017',
 }
];

function App() {
  const [active, setActive] = useState('');
  const [users, setUsers] = useState(arrayOfUsers);
  const [activeUser, setActiveUser] = useState({
    name: '',
    email: '',
    password: ''
  });

  const handleChange = (user: User) =>{
    setUsers([...users,user])
  };

  const handleActive = (newActive: string) => {
    setActive(newActive);
  };

  const handleActiveUser = (newUser: User) => {
    setActiveUser(newUser);
  }

  return (
    <Router>
      <div className = "welcome-auth-registration-block">
        <div className = 'header-block'>
            <div className = "header-block-title">
              <h1 className = "omks">One Million KBTU Stories</h1>
            </div>
            <div className = "header-block-btn">
            <Link to="/auth" className = "btn">
              <button className="btn" >Login</button>
            </Link>
            <Link to="/registration" className = "btn btn-sign-up">
              <button className="btn btn-sign-up" >Sign Up</button> 
            </Link>
            </div>
        </div>          
        <div className = 'main-block'>
        <Switch>
          <Route path='/registration' exact render={(props) => (<Registration {...props} initialUsers = {users} onChange = {handleChange} activeChange = {handleActive} />)}/>
          <Route path='/auth' render={(props) => (<Auth {...props} initialUsers = {users} activeChange = {handleActive} activeUserChange = {handleActiveUser} /> )} />
          <Route path='/Welcome' component={Welcome} render={(props) => (<Welcome {...props} activeUser = {activeUser} /> )} />
        </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
