import React, { ReactElement } from 'react'
import {User} from '../App';

interface Props {
    onChange: (user: User) => void;
    activeChange: (newActive: string) => void;
}

export default function Registration({onChange,activeChange }: Props): ReactElement {
  const newUser = {
      name: '',
      email: '',
      password: ''
  };
  
  const handleChange = (user: User) => {
    onChange(user);
    }

  const handleActive = (newActive: string) => {
    activeChange(newActive);
  }

  const handleSubmit = () => {
    console.log('You are registered!');
}
  
    return (
        <div>
            <div className = "input-label-block">
                <span>Name: </span><input type = "text" onChange = {e => { newUser.name = e.target.value}}/>
                <span>Email: </span><input type = "text" onChange = {e => { newUser.email = e.target.value}}/>
                <span>Password: </span><input type = "text" onChange = {e => { newUser.password = e.target.value}}/>
            </div>
            <div className= "btn-auth-block">
                <button className = 'btn' onClick = {()=> handleActive('')}>Cancel</button>
                <button className = 'btn' onClick = {handleSubmit}>Submit</button>
            </div>
        </div>
        
    )
}
